  --input: 32 15% 25%;
  --primary: 38 45% 65%;
  --primary-foreground: 32 20% 15%;
  --secondary: 32 15% 25%;
  --secondary-foreground: 43 36% 95%;
  --accent: 38 45% 65%;
  --accent-foreground: 32 20% 15%;
