import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Users, Bed, Bath } from "lucide-react";
import { Link } from "wouter";
import type { Property } from "@shared/schema";

interface PropertyCardProps {
  property: Property;
  index?: number;
  isAvailable?: boolean;
  showAvailability?: boolean;
  checkInDate?: string;
  checkOutDate?: string;
}

const PropertyCard = ({ property, index = 0, isAvailable = false, showAvailability = false, checkInDate, checkOutDate }: PropertyCardProps) => {
  // Build URL with date parameters if available
  const getPropertyUrl = () => {
    const baseUrl = `/properties/${property.id}`;
    if (checkInDate && checkOutDate) {
      return `${baseUrl}?checkIn=${checkInDate}&checkOut=${checkOutDate}`;
    }
    return baseUrl;
  };
  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6, delay: index * 0.1 }}
      className="property-card"
    >
      <Card className="luxury-card overflow-hidden luxury-shadow transition-all duration-700 group">
        <Link href={getPropertyUrl()}>
          <div className="relative overflow-hidden cursor-pointer">
            <img
              src={property.images[0]}
              alt={property.name}
              className="w-full h-72 object-cover transition-transform duration-700 group-hover:scale-110"
              loading="lazy"
              onError={(e) => {
                console.error('Failed to load image:', property.images[0]);
                // Fallback to a placeholder or retry
                e.currentTarget.src = '/logo.png';
              }}
              style={{
                filter: "contrast(1.08) saturate(1.15) brightness(1.03)"
              }}
            />
            <div className="absolute inset-0 bg-gradient-to-t from-luxury-brown/20 via-transparent to-transparent"></div>
          </div>
        </Link>

        <CardContent className="p-4 sm:p-6 lg:p-8">
          <div className="mb-4">
            <div className="flex items-start justify-between mb-2">
              <Link href={getPropertyUrl()}>
                <h3 className="text-xl luxury-subheading text-luxury-brown hover:text-luxury-gold transition-colors duration-300 leading-tight cursor-pointer">
                  {property.name}
                </h3>
              </Link>
              {showAvailability && (
                <div className={`inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium ml-2 ${
                  isAvailable 
                    ? 'bg-green-100 text-green-700' 
                    : 'bg-red-100 text-red-700'
                }`}>
                  <div className={`w-2 h-2 rounded-full ${
                    isAvailable ? 'bg-green-500' : 'bg-red-500'
                  }`}></div>
                  {isAvailable ? 'Available' : 'Not Available'}
                </div>
              )}
            </div>
            <p className="text-luxury-bronze text-sm mt-2 luxury-serif">{property.location}</p>
          </div>

          <p className="luxury-text mb-6 line-clamp-3 leading-relaxed">
            {property.description.split('.')[0]}.
          </p>

          <div className="flex flex-wrap items-center gap-4 sm:gap-6 text-sm luxury-text mb-6 py-4 border-t border-b border-luxury-gold/20">
            <div className="flex items-center">
              <Bed className="w-4 h-4 mr-2 text-luxury-gold" />
              <span className="font-medium">{property.bedrooms} Bedroom{property.bedrooms > 1 ? 's' : ''}</span>
            </div>
            <div className="flex items-center">
              <Bath className="w-4 h-4 mr-2 text-luxury-gold" />
              <span className="font-medium">{property.bathrooms} Bath</span>
            </div>
            <div className="flex items-center">
              <Users className="w-4 h-4 mr-2 text-luxury-gold" />
              <span className="font-medium">{property.maxGuests} Guests</span>
            </div>
          </div>

          <div className="flex items-center justify-end">
            <Link href={getPropertyUrl()}>
              <Button className="luxury-button group">
                <span className="group-hover:translate-x-1 transition-transform duration-300">View Details</span>
              </Button>
            </Link>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default PropertyCard;

client/src/components/reviews-section.ts.content:
import { useQuery } from "@tanstack/react-query";
import { Star } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { format } from "date-fns";
import type { Review } from "@shared/schema";

interface ReviewsSectionProps {
  propertyId?: number;
  featured?: boolean;
  className?: string;
  showTitle?: boolean;
  maxReviews?: number;
}

export function ReviewsSection({ 
  propertyId, 
  featured = false, 
  className = "",
  showTitle = true,
  maxReviews
}: ReviewsSectionProps) {
  const { data: reviews = [], isLoading } = useQuery<Review[]>({
    queryKey: propertyId ? ['/api/reviews', propertyId] : featured ? ['/api/reviews', 'featured'] : ['/api/reviews'],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (propertyId) params.append('propertyId', propertyId.toString());
      if (featured) params.append('featured', 'true');

      const response = await fetch(`/api/reviews?${params}`);
      if (!response.ok) throw new Error('Failed to fetch reviews');
      return response.json();
    }
  });

  if (isLoading) {
    return (
      <div className={`space-y-4 ${className}`}>
        {showTitle && <h2 className="text-2xl font-bold text-amber-900">Guest Reviews</h2>}
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {[1, 2, 3].map((i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader className="pb-3">
                <div className="h-4 bg-gray-200 rounded w-2/3"></div>
                <div className="h-3 bg-gray-200 rounded w-1/2"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-3 bg-gray-200 rounded"></div>
                  <div className="h-3 bg-gray-200 rounded w-4/5"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const displayReviews = maxReviews ? reviews.slice(0, maxReviews) : reviews;

  if (displayReviews.length === 0) {
    return (
      <div className={`text-center py-8 ${className}`}>
        {showTitle && <h2 className="text-2xl font-bold text-amber-900 mb-4">Guest Reviews</h2>}
        <p className="text-gray-600">No reviews yet. Be the first to share your experience!</p>
      </div>
    );
  }

  return (
    <div className={`space-y-6 ${className}`}>
      {showTitle && (
        <div className="text-center">
          <h2 className="text-3xl font-bold text-amber-900 mb-2">What Our Guests Say</h2>
          <p className="text-gray-600">Real experiences from verified guests</p>
        </div>
      )}

      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
        {displayReviews.map((review, index) => (
          <Card key={review.id} className="h-full flex flex-col luxury-card luxury-shadow group hover:scale-105 transition-all duration-500 border-0 relative overflow-hidden">
            {/* Luxury border accent */}
            <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-luxury-gold to-luxury-dark-gold"></div>

            <CardHeader className="pb-4 relative">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-1">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      className={`h-5 w-5 ${
                        i < review.rating 
                          ? "fill-yellow-400 text-yellow-400 drop-shadow-md" 
                          : "text-gray-300"
                      }`}
                    />
                  ))}
                </div>
                {review.verified && (
                  <Badge className="bg-gradient-to-r from-emerald-500 to-teal-600 text-white border-0 text-xs font-medium shadow-md">
                    âœ“ Verified Guest
                  </Badge>
                )}
              </div>

              {review.title && (
                <h3 className="font-bold text-xl text-luxury-brown leading-tight luxury-subheading mb-3">
                  {review.title}
                </h3>
              )}

              <div className="flex items-center justify-between">
                <div className="flex flex-col">
                  <span className="font-bold text-luxury-brown luxury-serif text-lg">{review.guestName}</span>
                  {review.guestLocation && (
                    <span className="text-luxury-light-brown text-sm font-medium">{review.guestLocation}</span>
                  )}
                </div>
                {review.stayDate && (
                  <div className="text-right">
                    <p className="text-xs text-luxury-light-brown uppercase tracking-wider font-medium">
                      {format(new Date(review.stayDate), 'MMM yyyy')}
                    </p>
                  </div>
                )}
              </div>
            </CardHeader>

            <CardContent className="flex-grow relative">
              <p className="text-gray-700 leading-relaxed text-sm font-normal">
                {review.content}
              </p>

              {/* Decorative bottom accent */}
              <div className="absolute bottom-0 left-0 w-12 h-px bg-gradient-to-r from-luxury-gold to-transparent mt-4"></div>
            </CardContent>
          </Card>
        ))}
      </div>

      {maxReviews && reviews.length > maxReviews && (
        <div className="text-center">
          <p className="text-gray-600">
            Showing {maxReviews} of {reviews.length} reviews
          </p>
        </div>
      )}
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/property-data.ts.content:
import type { Property } from "@shared/schema";

/**
 * Utility functions for working with property data
 */

/**
 * Format price for display
 */
export function formatPrice(price: string | number): string {
  const numPrice = typeof price === "string" ? parseFloat(price) : price;
  return new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  }).format(numPrice);
}

/**
 * Format rating for display
 */
export function formatRating(rating: string | number): string {
  const numRating = typeof rating === "string" ? parseFloat(rating) : rating;
  return numRating.toFixed(1);
}

/**
 * Get property category display name
 */
export function getCategoryDisplayName(category: string): string {
  const categoryMap: Record<string, string> = {
    beachfront: "Beachfront",
    villa: "Villa",
    mansion: "Mansion",
    penthouse: "Penthouse",
    retreat: "Retreat",
  };
  return categoryMap[category] || category;
}

/**
 * Filter properties by search term
 */
export function filterPropertiesBySearch(
  properties: Property[],
  searchTerm: string
): Property[] {
  if (!searchTerm.trim()) return properties;

  const term = searchTerm.toLowerCase();
  return properties.filter(
    (property) =>
      property.name.toLowerCase().includes(term) ||
      property.description.toLowerCase().includes(term) ||
      property.location.toLowerCase().includes(term) ||
      property.amenities.some((amenity) =>
        amenity.toLowerCase().includes(term)
      )
  );
}

/**
 * Filter properties by category
 */
export function filterPropertiesByCategory(
  properties: Property[],
  category: string
): Property[] {
  if (category === "all") return properties;
  return properties.filter((property) => property.category === category);
}

/**
 * Filter properties by price range
 */
export function filterPropertiesByPrice(
  properties: Property[],
  priceRange: string
): Property[] {
  if (priceRange === "all") return properties;

  return properties.filter((property) => {
    const price = parseFloat(property.pricePerNight);
    switch (priceRange) {
      case "0-500":
        return price < 500;
      case "500-1000":
        return price >= 500 && price <= 1000;
      case "1000-1500":
        return price >= 1000 && price <= 1500;
      case "1500+":
        return price > 1500;
      default:
        return true;
    }
  });
}

/**
 * Sort properties by different criteria
 */
export function sortProperties(
  properties: Property[],
  sortBy: "price-asc" | "price-desc" | "rating" | "name"
): Property[] {
  const sorted = [...properties];

  switch (sortBy) {
    case "price-asc":
      return sorted.sort(
        (a, b) => parseFloat(a.pricePerNight) - parseFloat(b.pricePerNight)
      );
    case "price-desc":
      return sorted.sort(
        (a, b) => parseFloat(b.pricePerNight) - parseFloat(a.pricePerNight)
      );
    case "rating":
      return sorted.sort(
        (a, b) => parseFloat(b.rating) - parseFloat(a.rating)
      );
    case "name":
      return sorted.sort((a, b) => a.name.localeCompare(b.name));
    default:
      return sorted;
  }
}

/**
 * Get properties suitable for a number of guests
 */
export function filterPropertiesByGuests(
  properties: Property[],
  guestCount: number
): Property[] {
  return properties.filter((property) => property.maxGuests >= guestCount);
}

/**
 * Get properties with specific amenities
 */
export function filterPropertiesByAmenities(
  properties: Property[],
  requiredAmenities: string[]
): Property[] {
  if (requiredAmenities.length === 0) return properties;

  return properties.filter((property) =>
    requiredAmenities.every((amenity) =>
      property.amenities.some((propAmenity) =>
        propAmenity.toLowerCase().includes(amenity.toLowerCase())
      )
    )
  );
}

/**
 * Get random featured properties
 */
export function getRandomFeaturedProperties(
  properties: Property[],
  count: number = 3
): Property[] {
  const featured = properties.filter((property) => property.featured);
  const shuffled = [...featured].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
}

/**
 * Calculate average rating for all properties
 */
export function calculateAverageRating(properties: Property[]): number {
  if (properties.length === 0) return 0;

  const total = properties.reduce(
    (sum, property) => sum + parseFloat(property.rating),
    0
  );
  return total / properties.length;
}

/**
 * Get property statistics
 */
export function getPropertyStatistics(properties: Property[]) {
  const totalProperties = properties.length;
  const averageRating = calculateAverageRating(properties);
  const averagePrice =
    properties.reduce(
      (sum, property) => sum + parseFloat(property.pricePerNight),
      0
    ) / totalProperties;

  const priceRange = {
    min: Math.min(
      ...properties.map((property) => parseFloat(property.pricePerNight))
    ),
    max: Math.max(
      ...properties.map((property) => parseFloat(property.pricePerNight))
    ),
  };

  const categoryCounts = properties.reduce((acc, property) => {
    acc[property.category] = (acc[property.category] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  return {
    totalProperties,
    averageRating,
    averagePrice,
    priceRange,
    categoryCounts,
  };
}

/**
 * Validate property data
 */
export function validateProperty(property: Partial<Property>): string[] {
  const errors: string[] = [];

  if (!property.name || property.name.trim().length === 0) {
    errors.push("Property name is required");
  }

  if (!property.description || property.description.trim().length < 10) {
    errors.push("Property description must be at least 10 characters");
  }

  if (!property.pricePerNight || parseFloat(property.pricePerNight) <= 0) {
    errors.push("Valid price per night is required");
  }

  if (!property.bedrooms || property.bedrooms < 1) {
    errors.push("Number of bedrooms must be at least 1");
  }

  if (!property.bathrooms || property.bathrooms < 1) {
    errors.push("Number of bathrooms must be at least 1");
  }

  if (!property.maxGuests || property.maxGuests < 1) {
    errors.push("Maximum guests must be at least 1");
  }

  if (!property.location || property.location.trim().length === 0) {
    errors.push("Location is required");
  }

  if (!property.images || property.images.length === 0) {
    errors.push("At least one image is required");
  }

  if (!property.amenities || property.amenities.length === 0) {
    errors.push("At least one amenity is required");
  }

  if (!property.category || property.category.trim().length === 0) {
    errors.push("Category is required");
  }

  return errors;
}

/**
 * Generate property URL slug
 */
export function generatePropertySlug(name: string): string {
  return name
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, "-")
    .replace(/(^-|-$)/g, "");
}

/**
 * Calculate nights between dates
 */
export function calculateNights(checkIn: string, checkOut: string): number {
  const checkInDate = new Date(checkIn);
  const checkOutDate = new Date(checkOut);
  const timeDiff = checkOutDate.getTime() - checkInDate.getTime();
  return Math.ceil(timeDiff / (1000 * 3600 * 24));
}

/**
 * Calculate total cost for a stay
 */
export function calculateTotalCost(
  pricePerNight: string | number,
  checkIn: string,
  checkOut: string,
  serviceFee: number = 0,
  taxRate: number = 0
): {
  nights: number;
  basePrice: number;
  serviceFee: number;
  tax: number;
  total: number;
} {
  const nights = calculateNights(checkIn, checkOut);
  const nightly = typeof pricePerNight === "string" ? parseFloat(pricePerNight) : pricePerNight;
  const basePrice = nightly * nights;
  const tax = basePrice * taxRate;
  const total = basePrice + serviceFee + tax;

  return {
    nights,
    basePrice,
    serviceFee,
    tax,
    total,
  };
}

